import React, { useState, useEffect } from 'react';
import axios from 'axios';
import moment from 'moment';



export default function VerReservas(){
    const [reservas, setReservas] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');

    useEffect(() => {
        loadReservas();
    }, []);

    const loadReservas = async() => {
        const result = await axios.get("http://localhost:8080/verreservas")
        setReservas(result.data);
    }

    const handleSearch = (e) => {
      setSearchTerm(e.target.value);
    };
  
    const filteredReserva= reservas.filter((reserva) => {
      const searchQuery = searchTerm.toLowerCase();
      return (
        reserva.nombre.toString().includes(searchQuery) ||
        reserva.dni.toString().includes(searchQuery)
      );
    });
if (sessionStorage.getItem('role')){
  return (
    <div class='container'>
       <div className='py-4'>
       <div className="mb-4">
          <input
            type="text"
            className="form-control"
            placeholder="Buscar..."
            value={searchTerm}
            onChange={handleSearch}
          />
        </div>
          {reservas.length > 0 && (
            // En caso de quwe haya reservas
            <table className="table border shadow">
              <thead>
                <tr>
                  <th scope="col">NÂº de mesa</th>
                  <th scope="col">Comentario</th>
                  <th scope="col">Capacidad</th>
                  <th scope="col">DNI</th>
                  <th scope="col">Nombre</th>
                  <th scope="col">Fecha</th>
                </tr>
              </thead>
              <tbody>
                {filteredReserva
                  .filter(reserva => moment(reserva.fecha).isSameOrBefore(moment().add(1, 'month'), 'day'))
                  .map((reserva, index) => {
                    const formattedFecha = moment(reserva.fecha).format('DD-MM-YYYY');
                    return (
                      <tr key={index}>
                        <th scope='row'>{reserva.mesa.numero}</th>
                        <td>{reserva.mesa.comentario}</td>
                        <td>{reserva.mesa.capacidad}</td>
                        <td>{reserva.dni}</td>
                        <td>{reserva.nombre}</td>
                        <td>{formattedFecha}</td>
                      </tr>
                    );
                  })}
              </tbody>
            </table>
          )}

          
          {reservas.length == 0 &&  (
            // En caso de que no haya reservas
            <div class="card noreserva">
              <div class="card-body">
                <p class="card-text">No hay reservas</p>
              </div>
            </div>
          )}
        </div>

        <button class='btn btn-outline-primary mx-2 volveratras botoncito'><a href="/homeadmin">Volver atrÃ¡s</a></button>
    </div>
  )
}
else{
    alert("No tienes permisos para ver esta pÃ¡gina!");
    window.location.href = '/loginUser';
}
}
