import React, { useState, useEffect } from 'react';
import axios from 'axios';


export default function VerPlatos(){
    const [platos, setPlatos] = useState([]);
    useEffect(() => {
        loadPlatos();
    }, []);

    const loadPlatos = async() => {
        const result = await axios.get("http://localhost:8080/verplatos")
        setPlatos(result.data);
    }



    const handleEstadoClick = async (platoId, estadoActual) => {
        const nuevoEstado = estadoActual == 1 ? 0 : 1;
    
        const updatedPlatos = platos.map((plato) => {
          if (plato.id === platoId) {
            return { ...plato, estadop: nuevoEstado };
          }
          return plato;
        });
    
        try {
          await axios.put(`http://localhost:8080/actualizarestado/${platoId}`, { estadop: nuevoEstado });
          setPlatos(updatedPlatos);
        } catch (error) {
          console.error('Error al actualizar el estado del plato', error);
        }
      };


  return (
    <div class='container'>
        <div class='py-4'>
        <table class="table border shadow">
            <thead>
                <tr>
                    <th scope="col">Nº de plato</th>
                    <th scope="col">Imagen</th>
                    <th scope="col">Nombre</th>
                    <th scope="col">Descripción</th>
                    <th scope="col">Precio</th>
                    <th scope="col">Especial</th>
                    <th scope="col">Estado</th>
                </tr>
            </thead>
            <tbody>
                {
                    platos.map((plato,index)=>(
                        <tr key={index}>
                            <th scope='row'>{plato.id}</th>
                            <td><a href="#">{plato.imagen}</a></td>
                            <td>{plato.titulo}</td>
                            <td>{plato.descripcion}</td>
                            <td>{plato.precio}</td>
                            <td>{plato.especial == 1 ? 'Sí' : 'No'}</td>
                            <td><a href="#" onClick={() => handleEstadoClick(plato.id, plato.estadop)}>{plato.estadop == 1 ? 'Activado' : 'Desactivado'}</a></td>
                            <td>
                                <button class='btn btn-outline-primary mx-2'>Editar</button>
                            </td>
                        </tr>
                    ))
                }
            </tbody>
        </table>
        </div>
    </div>
  )
}
