import React, { useState, useEffect } from 'react';
import axios from 'axios';


export default function VerPlatos(){
    const [platos, setPlatos] = useState([]);
    useEffect(() => {
        loadPlatos();
    }, []);

    const loadPlatos = async() => {
        const result = await axios.get("http://localhost:8080/verplatos")
        setPlatos(result.data);
    }

  return (
    <div classNameName='container'>
        <div classNameName='py-4'>
        <table classNameName="table border shadow">
            <thead>
                <tr>
                    <th scope="col">Nº de plato</th>
                    <th scope="col">Imagen</th>
                    <th scope="col">Nombre</th>
                    <th scope="col">Descripción</th>
                    <th scope="col">Precio</th>
                    <th scope="col">Especial</th>
                    <th scope="col">Estado</th>
                </tr>
            </thead>
            <tbody>
                {
                    platos.map((plato,index)=>(
                        <tr key={index}>
                            <th scope='row'>{index+1}</th>
                            <td>{plato.imagen}</td>
                            <td>{plato.titulo}</td>
                            <td>{plato.descripcion}</td>
                            <td>{plato.precio}</td>
                            <td>{plato.especial}</td>
                            <td>{plato.estado}</td>


                            <td>
                                <button classNameName='btn btn-outline-primary mx-2'>Editar</button>
                            </td>
                        </tr>
                    ))
                }
            </tbody>
        </table>
        </div>
    </div>
  )
}
