import React, { useState, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import Reserva from '../img/reserva.jpg';
import 'react-datepicker/dist/react-datepicker.css';

export default function Reservar(){
  const [numero, setNumPersonas] = useState('');
  const [fecha, setFechaReserva] = useState('');
  const [mostrarCalendario, setMostrarCalendario] = useState(false);
  const [mostrarSeleccionHora, setMostrarSeleccionHora] = useState(false);
  const [hora, setHoraReserva] = useState('');

  const handleNumPersonasChange = (e) => {
    setNumPersonas(e.target.value);
    setMostrarCalendario(true);
  };

  const handleFechaReservaChange = (date) => {
    setFechaReserva(date);
    setMostrarSeleccionHora(true);
  };

  const handleHoraReservaChange = (e) => {
    setHoraReserva(e.target.value);
  };

  const today = new Date();
  const maxDate = new Date();
  maxDate.setMonth(maxDate.getMonth() + 1);

  const filterDate = (date) => {
    return date.getDay() !== 2; // Filtrar los martes (2 representa el martes)
  };
  
  const handleReservaSubmit = () => {
    // Construir el objeto de datos a enviar al backend
    const reservaData = {
      numero,
      fecha,
      hora
    };

    // Realizar la solicitud POST al backend
    fetch('/buscarMesas', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(reservaData)
    })
      .then(response => {
        // Manejar la respuesta del backend según tus necesidades
        // Puede ser una redirección, mostrar un mensaje de éxito, etc.
        console.log('Solicitud de reserva enviada con éxito');
      })
      .catch(error => {
        // Manejar cualquier error que ocurra durante la solicitud
        console.error('Error al enviar la solicitud de reserva:', error);
      });
  };

  return (
    <div>
      <div className='fotoCarta'>
          <img src={Reserva} alt='Foto carta' />
          <div className="textoSobreImagenRe">
            <p>Reserva tu mesa y disfruta de nuestros servicios</p>
          </div>
      </div>
      <div className='containerReserva'>
        <label>Número de Personas:
          <input type="number" value={numero} min={1} max={20} onChange={handleNumPersonasChange}/>
        </label>
        {mostrarCalendario && (
          <div>
            <label>Fecha de Reserva:<DatePicker selected={fecha} onChange={handleFechaReservaChange} minDate={today} maxDate={maxDate} placeholderText="Seleccione una fecha"/></label>
          </div>
        )}
        {mostrarSeleccionHora && (
          <div>
            <label>Hora de Reserva:
              <input type="time" value={hora} onChange={handleHoraReservaChange} />
            </label>
          </div>
        )}
        {hora && (
          <button onClick={handleReservaSubmit}>Enviar Reserva</button>
        )}
      </div>
    </div>
  );
};
