import React, { useState, useEffect } from 'react';
import axios from 'axios';


export default function VerPlatos(){
    const [platos, setPlatos] = useState([]);
    useEffect(() => {
        loadPlatos();
    }, []);

    const loadPlatos = async() => {
        const result = await axios.get("http://localhost:8080/verplatos")
        setPlatos(result.data);
    }

  return (
    <div class='container'>
        <div class='py-4'>
        <table class="table border shadow">
            <thead>
                <tr>
                    <th scope="col">Nº de plato</th>
                    <th scope="col">Imagen</th>
                    <th scope="col">Nombre</th>
                    <th scope="col">Descripción</th>
                    <th scope="col">Precio</th>
                    <th scope="col">Especial</th>
                    <th scope="col">Estado</th>
                </tr>
            </thead>
            <tbody>
                {
                    platos.map((plato,index)=>(
                        <tr key={index}>
                            <th scope='row'>{plato.id}</th>
                            <td><a href="#">{plato.imagen}</a></td>
                            <td>{plato.titulo}</td>
                            <td>{plato.descripcion}</td>
                            <td>{plato.precio}</td>
                            <td>{plato.especial == 1 ? 'Sí' : 'No'}</td>
                            <td>{plato.estadop == 1 ? 'Activado' : 'Desactivado'}</td>

                            <td>
                                <button class='btn btn-outline-primary mx-2'>Editar</button>
                            </td>
                        </tr>
                    ))
                }
            </tbody>
        </table>
        </div>
    </div>
  )
}
