import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function PrincipalPage() {
    //Ofertas especiales
    const [reservas, setReservas] = useState([]);
    useEffect(() => {
        loadReservas();
    }, []);

    const loadReservas = async() => {
        const result = await axios.get("http://localhost:8080/verreservas")
        setReservas(result.data);
    }

    return(
        <div class="container">
            {reservas
                  .filter(reserva => moment(reserva.fecha).isSameOrBefore(moment().add(1, 'month'), 'day'))
                  .map((reserva, index) => {
                    const formattedFecha = moment(reserva.fecha).format('DD-MM-YYYY');
                    return (
                      <tr key={index}>


                        <div class="card mb-3">
                        <img class="card-img-top" src="#" alt="Card image cap"></img>
                        <div class="card-body">
                            <h5 class="card-title">Card title</h5>
                            <p class="card-text">This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</p>
                            <p class="card-text"><small class="text-muted">Last updated 3 mins ago</small></p>
                        </div>
                        </div>

                        <th scope='row'>{reserva.mesa.numero}</th>
                        <td>{reserva.mesa.comentario}</td>
                        <td>{reserva.mesa.capacidad}</td>
                        <td>{reserva.dni}</td>
                        <td>{reserva.nombre}</td>
                        <td>{formattedFecha}</td>
                      </tr>
                    );
                  })}
        </div>
    )
}
