import React from 'react';

const Carta = () => {
  const menuItems = [
    {
      id: 1,
      title: 'Plato 1',
      description: 'Descripción del plato 1',
      price: '$10.99',
      image: 'ruta-de-la-imagen-1.jpg',
    },
    {
      id: 2,
      title: 'Plato 2',
      description: 'Descripción del plato 2',
      price: '$12.99',
      image: 'ruta-de-la-imagen-2.jpg',
    },
    // Agrega más objetos para más platos
  ];

  return (
    <div className="container">
      <h2 className="mt-5 mb-3">Carta de nuestro restaurante</h2>
      <div className="row">
        {menuItems.map((item) => (
          <div className="col-lg-4 col-md-6 mb-4" key={item.id}>
            <div className="card">
              <img src={item.image} className="card-img-top" alt={item.title} />
              <div className="card-body">
                <h5 className="card-title">{item.title}</h5>
                <p className="card-text">{item.description}</p>
                <p className="card-text">Precio: {item.price}</p>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Menu;
