import React, { useState, useEffect } from 'react';
import axios from 'axios';

export default function PrincipalPage() {
    //Ofertas especiales
    const [platos, setPlatos] = useState([]);
    useEffect(() => {
        loadPlatos();
    }, []);

    const loadPlatos = async() => {
        const result = await axios.get("http://localhost:8080/verplatos")
        setPlatos(result.data);
    }


    return(
        <div class="container">
            {platos
                  .filter(plato=> plato.especial==1)
                  .map((plato, index) => {
                    return (
                        <div class="card mb-3">
                        <img class="card-img-top" src="C:\Users\diana\Desktop\JAVA\REACT\src\img\platos.png" alt="Card image cap"></img>
                        <div class="card-body">
                            <h5 class="card-title">{plato.titulo}</h5>
                            <p class="card-text">{plato.descripcion}</p>
                            <p class="card-text"><small class="text-muted">{plato.precio}â‚¬</small></p>
                        </div>
                        </div>
                    );
                  })}
        </div>
    )
}
