import axios from "axios";
import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function CrearPlato() {
  const alerta = document.getElementById("alerta");
  // alerta.style.display = "none";
  let navigate = useNavigate();
  
  const [plato, setPlato] = useState({
    id: "",
    titulo: "",
    descripcion: "",
    imagen: "",
    precio: "",
    estadop: true,
    especial: false,
  });

  const [errorMessage, setErrorMessage] = useState("");
  const [alertMessage, setAlertMessage] = useState("");

  const { id, titulo, descripcion, imagen, precio, estadop, especial} = plato;

  const onInputChange = (e) => {
    setPlato({ ...plato, [e.target.name]: e.target.value });
  };

  const onCheckboxChange = (e) => {
    setPlato({ ...plato, [e.target.name]: e.target.checked });
  };

  const onSubmit = async (e) => {
    e.preventDefault();
    try {
        const response = await axios.post("http://localhost:8080/crearplato", { ...plato, estadop: Boolean(plato.estadop) });
        navigate("/homeadmin");
    } catch (error) {
        console.log(error.response.data);
        if(error.response.data == false){
          // alerta.style.display = "block";
          alerta.innerHTML = "¡Ya existe un plato con el mismo nombre!";
        }
        // console.error("La respuesta es : "+ error.response.data);
        // setErrorMessage("Te response is : "+ error.response.data.error); // guardar el mensaje de error en un estado de React
    }
};

  // más adelante en el código, mostrar el mensaje de error en una alerta
  // {errorMessage && (
  //     <div className="alert alert-danger" role="alert">
  //         {errorMessage}
  //     </div>
  // )}

  return (
    <div className="container">
      <div className="row">
        <div className="col-md-6 offset-md-3 border rounded p-4 mt-2 shadow">
          <h2 className="text-center m-4">Añadir plato</h2>
            <div className="alert alert-danger" role="alert" id="alerta">
            </div>
          <form onSubmit={(e) => onSubmit(e)}>
            <div className="mb-3">
              <label htmlFor="titulo" className="form-label">titulo</label>
              <input type={"text"} className="form-control" placeholder="titulo" name="titulo" value={titulo} onChange={(e) => onInputChange(e)} required/>
            </div>

            <div className="mb-3">
              <label htmlFor="descripcion" className="form-label">descripcion</label>
              <textarea className="form-control" rows="3" placeholder="descripcion" name="descripcion" value={descripcion} onChange={(e) => onInputChange(e)} required></textarea>
            </div>

            <div className="mb-3">
              <label htmlFor="imagen" className="form-label">imagen</label>
              <input type={"file"} className="form-control" name="imagen" value={imagen} onChange={(e) => onInputChange(e)} required/>
            </div>

            <div className="mb-3">
              <label htmlFor="precio" className="form-label">precio</label>
              <input type={"number"} className="form-control" placeholder="precio" name="precio" value={precio} onChange={(e) => onInputChange(e)} required/>
            </div>

            <div className="mb-3 form-check">
              <input type="checkbox" className="form-check-input" name="especial" checked={especial} onChange={(e) => onCheckboxChange(e)}/>
              <label className="form-check-label" htmlFor="especial">especial</label>
            </div>
            <button type="submit" className="btn btn-outline-primary">Submit</button>
          </form>
        </div>
      </div>
      
      <button class='btn btn-outline-primary mx-2 volveratras'><a href="/homeadmin">Volver atrás</a></button>
    </div>
  );
}
