import React from 'react';
import { useState } from 'react';
// import { Form, Button } from 'react-bootstrap';

export default function CrearPlato() {
  const [descripcion, setDescripcion] = useState('');
  const [especial, setEspecial] = useState(false);
  const [imagen, setImagen] = useState('');
  const [precio, setPrecio] = useState(0);
  const [titulo, setTitulo] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    // Aquí podrías enviar los datos del formulario a tu servidor o hacer cualquier otra cosa que necesites
  }

  return (
    <Form onSubmit={handleSubmit}>
      <Form.Group className="mb-3" controlId="titulo">
        <Form.Label>Título</Form.Label>
        <Form.Control type="text" placeholder="Introduce el título del plato" value={titulo} onChange={(e) => setTitulo(e.target.value)} />
      </Form.Group>
      <Form.Group className="mb-3" controlId="descripcion">
        <Form.Label>Descripción</Form.Label>
        <Form.Control type="text" placeholder="Introduce la descripción del plato" value={descripcion} onChange={(e) => setDescripcion(e.target.value)} />
      </Form.Group>
      <Form.Group className="mb-3" controlId="especial">
        <Form.Check type="checkbox" label="Plato especial" checked={especial} onChange={(e) => setEspecial(e.target.checked)} />
      </Form.Group>
      <Form.Group className="mb-3" controlId="imagen">
        <Form.Label>Imagen (link)</Form.Label>
        <Form.Control type="text" placeholder="Introduce el link de la imagen del plato" value={imagen} onChange={(e) => setImagen(e.target.value)} />
      </Form.Group>
      <Form.Group className="mb-3" controlId="precio">
        <Form.Label>Precio</Form.Label>
        <Form.Control type="number" placeholder="Introduce el precio del plato" value={precio} onChange={(e) => setPrecio(parseFloat(e.target.value))} />
      </Form.Group>
      <Button variant="primary" type="submit">
        Crear plato
      </Button>
    </Form>
  );
}
