import React from 'react';
import { useState } from 'react';
// import { Form, Button } from 'react-bootstrap';

export default function CrearPlato() {
  const [descripcion, setDescripcion] = useState('');
  const [especial, setEspecial] = useState(false);
  const [imagen, setImagen] = useState('');
  const [precio, setPrecio] = useState(0);
  const [titulo, setTitulo] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    // Aquí podrías enviar los datos del formulario a tu servidor o hacer cualquier otra cosa que necesites
  }

  return <div className="container">
  <div className="row">
      <div className="col-md-6 offset-md-3 border rounded p-4 mt-2 shadow">
          <h2 className="text-center m-4">Register User</h2>
          <form onSubmit={(e)=>onSubmit(e)}>
              <div className="mb-3">
                  <label htmlFor="Name" className="form-label">Name</label>
                  <input type={"text"} className="form-control" placeholder="Name" name="name" value={name} onChange={(e)=>onInputChange(e)}/>
              </div>
              <div className="mb-3">
                  <label htmlFor="Name" className="form-label">Username</label>
                  <input type={"text"} className="form-control" placeholder="Username" name="username" value={username} onChange={(e)=>onInputChange(e)}/>
              </div>
              <div className="mb-3">
                  <label htmlFor="Name" className="form-label">Email</label>
                  <input type={"text"} className="form-control" placeholder="Email" name="email" value={email} onChange={(e)=>onInputChange(e)}/>
              </div>
              <button type="submit" className="btn btn-outline-primary">Submit</button>
          </form>
      </div>
  </div>
</div>
}
