import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Platos from './../img/platos.png';

export default function PrincipalPage() {
    //Ofertas especiales
    const [platos, setPlatos] = useState([]);
    useEffect(() => {
        loadPlatos();
    }, []);

    const loadPlatos = async() => {
        const result = await axios.get("http://localhost:8080/verplatos")
        setPlatos(result.data);
    }


    return(
        <div class="o">
            {platos
                  .filter(plato=> plato.especial==1)
                  .map((plato, index) => {
                    return (
                        <div class="ofertas">
                        <img src={Platos} />
                        <div class="texto-encima">Texto</div>
                        <div class="centrado">Centrado</div>
                        </div>
                    );
                  })}
        </div>
    )
}
