import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Platos from './../img/platos.png';

export default function PrincipalPage() {
    //Ofertas especiales
    const [platos, setPlatos] = useState([]);
    useEffect(() => {
        loadPlatos();
    }, []);

    const loadPlatos = async() => {
        const result = await axios.get("http://localhost:8080/verplatos")
        setPlatos(result.data);
    }


    return(
        <div id="contenedorpaginaprincipal">
            <div class="contenedorofertas">
                <h1>Ofertas especiales</h1>
                <div class="ofertas">
                    {platos
                        .filter(plato=> plato.especial==1 && plato.estadop==1)
                        .map((plato, index) => {
                            return (
                                <div class="card mb-3">
                                    <img class="card-img-top" src={Platos} alt="Card image cap"></img>
                                    <div class="card-body">
                                        <h5 class="card-title">{plato.titulo}</h5>
                                        <p class="card-text">{plato.descripcion}</p>
                                        <p class="card-text"><small class="text-muted">{plato.precio}€</small></p>
                                    </div>
                                </div>
                            );
                        })}
                </div>
            </div>
            <div class="resenyas">
                <h1>Reseñas</h1>
                <div class="containergeneral">
                <input class="form-control" type="text" placeholder="Deja una reseña"></input>
                <button type="submit" className="btn btn-outline-primary">Submit</button>
                </div>
            </div>
        </div>
    )

}
